package com.fdm.academy;import static org.junit.Assert.*;import static org.mockito.Mockito.*;import org.junit.*;import com.fdm.academy.storage.*;public class StorageTest{	@Test	public void testApplicationCallsStoreMethodInStorage(){				Storage storage = mock(Storage.class);		Object object = mock(Object.class);		Application app = new Application(storage);		app.process(object);		verify(storage,times(1)).store(eq(object));			}	}